	            // Note that we approximate 1/3 as 3/8 and 2/3 as 5/8 for
	            // speed.  This also appears to be what the hardware DXT
	            // decoder in many GPUs does :)
	            c[2] = ((5 * r0 + 3 * r1) >> 3)
	                | (((5 * g0 + 3 * g1) >> 3) & 0x7e0)
	                | (((5 * b0 + 3 * b1) >> 3) & 0xf800);
