	                cam_coc_fg.dof_bokeh = cam_render.dof_bokeh;
	                cam_coc_fg.dof_bokeh_intensity = cam_render.dof_bokeh_intensity;
	                cam_coc_fg.dof_foreground_blur = cam_render.dof_foreground_blur;
