	        hyst_prev_state: 0,
	        // adaptive hysteresis intervals for dist_min and dist_max borders
	        hyst_interval_min: 0,
	        hyst_interval_max: 0,
	        // check distance to change LODs immediately without smoothing (teleporting case)
	        prev_dist: 0
