	exports.TYPE_HMD_RIGHT = TYPE_HMD_RIGHT;

	// contolled by low-level set_look_at()
	exports.MS_STATIC = 0;

	// controlled by keyboard key directions:
	exports.MS_TARGET_CONTROLS = 2;

	// controlled by keyboard key directions
	exports.MS_EYE_CONTROLS = 3;

