	var STEREO_CONV_DIST = 6.0;
	// left-right eye distance (1/30 convergence)
	var STEREO_EYE_DIST = 0.065;

	var DEF_ORTHO_SCALE = 2.5;
	var DEF_PERSP_FOV   = m_util.deg_to_rad(40);
	var DEF_PERSP_NEAR  = 0.1;
	var DEF_PERSP_FAR   = 1000;

