	                    var axis = (Math.abs(hover_angle) >= Math.PI / 4) ? m_util.AXIS_MY : m_util.AXIS_Z;
	                    trans_hover_cam_horiz_local(obj, axis,
	                            velocity.trans * HOVER_KEY_TRANS_FACTOR * elapsed);
